const repl = global.get('repl');

// Copy tiddlers from wiki to web client
function copyTiddlers(msg) {
	var sender = Object.assign({}, msg.wiki.senderTiddler);
	sender.ioResult = '';

	var gotError = false;
	if (!sender.ioFromWiki) {
		sender.ioResult = `Error: Need the source wiki name to copy the tiddlers from.`;
		gotError = true;
	}
	else if (!sender.ioToWiki) {
		sender.ioResult = `Error: Need the destination wiki name to copy the tiddlers to`;
		gotError = true;
	}
	else if (!sender.ioFilter) {
		sender.ioResult = `Error: A filter to select the tiddlers to copy is required`;
		gotError = true;
	}
	else if (sender.ioFromWiki === sender.ioToWiki) {
		sender.ioResult = `Error: Can not copy to the same wiki.`;
		gotError = true;
	}

	// copy tiddlers
	if (!gotError) {
		var $from = repl.get$tw(sender.ioFromWiki);
		var $to = repl.get$tw(sender.ioToWiki);
		var tiddlers = $from.utils.parseJSONSafe(
			$from.wiki.getTiddlersAsJson(sender.ioFilter), []);
		tiddlers.forEach(tiddler => {
			$to.wiki.addTiddler(new $to.Tiddler(tiddler));
			// send system tiddlers to client via sockets
			if (/^\$:\//.test(tiddler.title)) {
				msg.wiki.resultTiddlers.push(tiddler);
			}
		})
		sender.ioResult = `${tiddlers.length} tiddlers copied from ${sender.ioFromWiki} to ${sender.ioToWiki}`;
	}	

	msg.wiki.resultTiddlers.push(sender);
	repl.$refreshClients(sender.ioToWiki);
	return msg;
}

return copyTiddlers(msg);

const repl = global.get('repl');

function resetIoFields(msg) {
    var clean = {};
    var sender = Object.assign({}, msg.wiki.senderTiddler);

    if (sender.title === 'project') {
        sender.ioResult = `Reset of pocket-io fields is not allowed on the 'Project Managment' tiddler. They are required for it to work.`
        msg.wiki.resultTiddlers.push(sender);
        return msg;
    }

    for (const key in sender) {
        if (/^io/.test(key)) {
            continue;
        }
        clean[key] = sender[key];
    }

    msg.wiki.resultTiddlers.push(clean);
    return msg;
}

return resetIoFields(msg);

